//Program that shows the use of conditional operator

package operatorsDemo;

public class ConditionalOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	      int a=10,b=20;
	      
	     int big;
	         big=a<b?a:b;
	         System.out.println(big);
	}

}

//output:
//10