//Program to show the use of increment and decrement operator

package operatorsDemo;

public class Incre_Decre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int x=10;
           
           System.out.println(x++);
          
           System.out.println(++x);
           
           System.out.println(x--);
           
           System.out.println(--x);
	}

}

/*output:
10
12
12
10*/
