//Program for left shift and right shift operator 

package operatorsDemo;

public class LeftShift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	   System.out.println(64>>2);      //right shift 
	   
	   System.out.println(64<<2);     //left shift
	}

}
 
//output:
//16
//256
