//Program to show the use of logical operators.

package operatorsDemo;

public class Operator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		boolean a=true;
		boolean b=false;
		
		boolean c=(a||b);
		boolean d=(a&&b);
		System.out.println(c);
		System.out.println(d);
	}

}

/*output:
true
false */