//Program to show the use of Bitwise AND Operator

package operatorsDemo;

public class Program_to_show_the_use_of_Bitwise_AND_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 int a=10;
		 int b=5;
		 int c=20;
		 
		 System.out.println(a<b & a<c);    //false & true = false
	}

}

//output:
//false