//Program that shows the use of relational operator

package operatorsDemo;

public class RelationalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=10,b=20;
		  
        System.out.println("a==b "+(a==b));
        System.out.println("a!=b "+(a!=b));
        System.out.println("a>b "+(a>b));
        System.out.println("a<b "+(a<b));
        System.out.println("a>=b "+(a>=b));
        System.out.println("a<=b "+(a<=b));
		       
	}
}

/*output:
a==b false
a!=b true
a>b false
a<b true
a>=b false
a<=b true */